223m/\\\033[39m\033[38;5;230m \\ \033[39m\033[38;5;231m\\ \033[39m\033[38;5;231m\\L\033[39m\033[38;5;253m\\ \\\033[39m\033[38;5;195m \\\033[39m\033[38;5;153m \\ \033[39m\033[38;5;117m\\ \033[39m\033[38;5;74m\\ \033[39m\033[38;5;32m\\ \\\033[39m\033[38;5;25m/\\\033[39m\033[38;5;24m \\ \033[39m\033[39m\n\033[38;5;88m   \033[39m\033[38;5;124m\\ \033[39m\033[38;5;160m\\_\\\033[39m\033[38;5;203m  \033[39m\033[38;5;209m \\\033[39m\033[38;5;216m \\_\033[39m\033[38;5;223m\\ \033[39m\033[38;5;230m\\_\\\033[39m\033[38;5;231m \\\033[39m\033[38;5;231m__\033[39m\033[38;5;253m__/\033[39m\033[38;5;195m  \033[39m\033[38;5;153m\\ \\\033[39m\033[38;5;117m_\\\033[39m\033[38;5;74m \\\033[39m\033[38;5;32m \\_\033[39m\033[38;5;25m\\ \033[39m\033[38;5;24m\\_\\\033[39m\033[39m\n\033[38;5;88m   \033[39m\033[38;5;124m \\\033[39m\033[38;5;160m/_/\033[39m\033[38;5;203m  \033[39m\033[38;5;209m  \033[39m\033[38;5;216m\\/_\033[39m\033[38;5;223m/\\\033[39m\033[38;5;230m/_/\033[39m\033[38;5;231m\\/\033[39m\033[38;5;231m__\033[39m\033[38;5;253m_/ \033[39m\033[38;5;195m  \033[39m\033[38;5;153m \\/\033[39m\033[38;5;117m_/\033[39m\033[38;5;74m  \033[39m\033[38;5;32m\\/_\033[39m\033[38;5;25m/\\\033[39m\033[38;5;24m/_/\033[39m\033[39m\n")

